tables.table_schema:
CREATE TABLE package_deals (
    deal_id INT PRIMARY KEY,
    deal_name TEXT,
    destination TEXT,
    price DECIMAL(10, 2)
);

CREATE TABLE package_deals (
    package_id UUID PRIMARY KEY,
    package_name TEXT,
    destination TEXT,
    price DECIMAL
);

CREATE TABLE availability (
    availability_id UUID PRIMARY KEY,
    package_id UUID,
    available_date DATE,
    available_qty INT
);

CREATE TABLE pricing (
    pricing_id UUID PRIMARY KEY,
    package_id UUID,
    base_price DECIMAL,
    discount DECIMAL
);

CREATE TABLE bookings (
    booking_id UUID PRIMARY KEY,
    package_id UUID,
    booking_date DATE,
    customer_name TEXT
);

CREATE TABLE package_deals (
	deal_id INT PRIMARY KEY,
	deal_name VARCHAR(255),
	description TEXT,
	price DECIMAL(10, 2)
);

CREATE TABLE travel_agencies (
	agency_id INT PRIMARY KEY,
	agency_name VARCHAR(255),
	location VARCHAR(255)
);
