tables.table_schema:
CREATE TABLE agreement_payments (
    payment_id UUID PRIMARY KEY,
    agreement_id UUID REFERENCES subcontractor_agreements(agreement_id),
    payment_amount DECIMAL,
    payment_date DATE
);

CREATE TABLE subcontractor_agreements (
    agreement_id INT PRIMARY KEY,
    subcontractor_name VARCHAR(50),
    contract_details TEXT,
    payment_terms TEXT
);

CREATE TABLE payment_history (
    payment_id UUID PRIMARY KEY,
    agreement_id UUID,
    payment_date TIMESTAMP,
    amount DECIMAL(10, 2)
);

CREATE TABLE subcontractor_ratings (
    rating_id UUID PRIMARY KEY,
    subcontractor_id UUID,
    project_id UUID,
    rating INT
);
