tables.table_schema:
CREATE TABLE ArchitecturalDrawings (
   id INT PRIMARY KEY,
   drawing_name VARCHAR(255),
   drawing_type VARCHAR(50),
   drawing_size INT
);

CREATE TABLE DesignSpecifications (
   id INT PRIMARY KEY,
   spec_name VARCHAR(255),
   spec_type VARCHAR(50),
   spec_version FLOAT
);

CREATE TABLE Drawings (
    id UUID PRIMARY KEY,
    title VARCHAR(255),
    architect VARCHAR(255),
    date_created TIMESTAMP
);

CREATE TABLE Blueprints (
    id UUID PRIMARY KEY,
    blueprint_name VARCHAR(255),
    engineer VARCHAR(255),
    date_created TIMESTAMP
);

CREATE TABLE DesignSpecifications (
    id UUID PRIMARY KEY,
    spec_name VARCHAR(255),
    designer VARCHAR(255),
    date_created TIMESTAMP
);

CREATE TABLE Reference (
    id UUID PRIMARY KEY,
    reference_name VARCHAR(255),
    type VARCHAR(255),
    abstract TEXT
);
