tables.table_schema:
CREATE TABLE stakeholders (
  id INT PRIMARY KEY,
  name VARCHAR,
  email VARCHAR,
  phone VARCHAR
);

CREATE TABLE partners (
  id INT PRIMARY KEY,
  name VARCHAR,
  email VARCHAR,
  phone VARCHAR
);

CREATE TABLE Events (
    event_id INT PRIMARY KEY,
    event_name VARCHAR(100),
    event_date DATE,
    location VARCHAR(100)
);
