tables.table_schema:
CREATE TABLE fashion_assets (
    id INT PRIMARY KEY,
    image_url VARCHAR(255),
    media_type VARCHAR(20),
    company_id INT
);

CREATE TABLE fashion_images (
    image_id INT PRIMARY KEY,
    image_url VARCHAR(255),
    image_description TEXT,
    company_id INT
);

CREATE TABLE fashion_company_images (
    image_id SERIAL PRIMARY KEY,
    image_url VARCHAR(255) NOT NULL,
    category VARCHAR(50),
    upload_date TIMESTAMP
);

CREATE TABLE fashion_companies (
  company_id INT PRIMARY KEY,
  company_name VARCHAR(50),
  company_location VARCHAR(100),
  PRIMARY KEY (company_id)
);

CREATE TABLE image_categories (
  category_id INT PRIMARY KEY,
  category_name VARCHAR(50),
  category_description VARCHAR(200)
);

CREATE TABLE Company (
    company_id UUID PRIMARY KEY,
    company_name VARCHAR(255) NOT NULL,
    location VARCHAR(255) NOT NULL
);

CREATE TABLE Brand (
    brand_id UUID PRIMARY KEY,
    brand_name VARCHAR(255) NOT NULL,
    company_id UUID REFERENCES Company(company_id) NOT NULL
);

CREATE TABLE Image (
    image_id UUID PRIMARY KEY,
    image_url TEXT NOT NULL,
    brand_id UUID REFERENCES Brand(brand_id) NOT NULL
);

CREATE TABLE Category (
    category_id UUID PRIMARY KEY,
    category_name VARCHAR(255) NOT NULL
);

CREATE TABLE ImageCategory (
    image_id UUID REFERENCES Image(image_id) NOT NULL,
    category_id UUID REFERENCES Category(category_id) NOT NULL
);
