tables.table_schema:
CREATE TABLE fashion_trends (
    trend_id INT PRIMARY KEY,
    trend_name VARCHAR(255),
    start_date DATE,
    end_date DATE
);

CREATE TABLE HistoricalData (
    id INT PRIMARY KEY,
    trend_id INT,
    year YEAR,
    sales_volume INT
);

CREATE TABLE TrendForecasts (
    id INT PRIMARY KEY,
    trend_id INT,
    forecast_date DATE,
    forecast_description TEXT
);

CREATE TABLE fashion_brands (
    brand_id INT PRIMARY KEY,
    brand_name VARCHAR(50) NOT NULL,
    founded_year INT
);

CREATE TABLE clothing_categories (
    category_id INT PRIMARY KEY,
    category_name VARCHAR(50) NOT NULL
);

CREATE TABLE trend_forecasts (
    forecast_id INT PRIMARY KEY,
    forecast_date DATE,
    trend_description TEXT
);
