tables.table_schema:
CREATE TABLE DesignSketches (
    sketch_id UUID PRIMARY KEY,
    designer_name VARCHAR(255) NOT NULL,
    sketch_description TEXT,
    sketch_image BYTEA
);

CREATE TABLE FabricDetails (
    fabric_id UUID PRIMARY KEY,
    fabric_type VARCHAR(50) NOT NULL,
    fabric_color VARCHAR(50),
    fabric_price DECIMAL(10, 2)
);

CREATE TABLE fashion_designer_sketches (
    sketch_id SERIAL PRIMARY KEY,
    designer_name TEXT,
    sketch_description TEXT,
    sketch_image BYTEA
);

CREATE TABLE fashion_designer_patterns (
    pattern_id SERIAL PRIMARY KEY,
    designer_name TEXT,
    pattern_type TEXT,
    pattern_image BYTEA
);

CREATE TABLE Designers (
    designer_id UUID PRIMARY KEY,
    full_name VARCHAR(100) NOT NULL,
    brand_name VARCHAR(100),
    bio TEXT
);

CREATE TABLE Collections (
    collection_id UUID PRIMARY KEY,
    designer_id UUID REFERENCES Designers(designer_id),
    collection_name VARCHAR(100) NOT NULL,
    release_date DATE
);

CREATE TABLE Design_Sketches (
    sketch_id UUID PRIMARY KEY,
    collection_id UUID REFERENCES Collections(collection_id),
    sketch_image BYTEA,
    description TEXT
);

CREATE TABLE Patterns (
    pattern_id UUID PRIMARY KEY,
    collection_id UUID REFERENCES Collections(collection_id),
    pattern_image BYTEA,
    fabric_type VARCHAR(50)
);

CREATE TABLE Fabrics (
    fabric_id UUID PRIMARY KEY,
    fabric_type VARCHAR(50) NOT NULL,
    color VARCHAR(50),
    material TEXT
);
