tables.table_schema:
CREATE TABLE ThirdPartyApplications (
  app_id UUID PRIMARY KEY,
  app_name VARCHAR(255) NOT NULL,
  app_description TEXT,
  crm_api_key VARCHAR(100) UNIQUE
);

CREATE TABLE ThirdPartyApplications (
    app_id INT PRIMARY KEY,
    app_name VARCHAR(255) NOT NULL,
    app_description TEXT
);

CREATE TABLE CRMSystem (
    crm_id INT PRIMARY KEY,
    crm_name VARCHAR(255) NOT NULL,
    crm_address VARCHAR(255)
);

CREATE TABLE DataMappings (
    mapping_id INT PRIMARY KEY,
    app_id INT,
    crm_id INT,
    mapping_details JSON
);

CREATE TABLE crm_applications (
    id SERIAL PRIMARY KEY,
    app_name VARCHAR(50) NOT NULL,
    app_description TEXT
);

CREATE TABLE crm_connections (
    id SERIAL PRIMARY KEY,
    connection_id VARCHAR(50) NOT NULL,
    connection_type VARCHAR(20) NOT NULL,
    last_sync TIMESTAMP
);

CREATE TABLE crm_sync_logs (
    id SERIAL PRIMARY KEY,
    sync_id SERIAL,
    log_message TEXT,
    log_timestamp TIMESTAMP
);

CREATE TABLE crm_sync_errors (
    id SERIAL PRIMARY KEY,
    sync_id SERIAL,
    error_message TEXT,
    error_timestamp TIMESTAMP
);
