tables.table_schema:
CREATE TABLE waste_collection_routes (
  route_id SERIAL PRIMARY KEY,
  route_name VARCHAR(100) NOT NULL,
  pickup_schedule VARCHAR(50) NOT NULL,
  truck_id INT REFERENCES trucks(truck_id)
);

CREATE TABLE waste_routes (
  route_id INT PRIMARY KEY,
  route_name VARCHAR(50),
  pickup_schedule TIMESTAMP,
  truck_id INT
);

CREATE TABLE waste_pickup_locations (
  location_id INT PRIMARY KEY,
  location_name VARCHAR(50),
  address TEXT,
  route_id INT
);

CREATE TABLE WasteRoutes (
    route_id INT PRIMARY KEY,
    route_name VARCHAR(50) NOT NULL,
    pickup_schedule TIMESTAMP NOT NULL
);

CREATE TABLE WastePickup (
    pickup_id INT PRIMARY KEY,
    route_id INT NOT NULL,
    pickup_date DATE NOT NULL,
    pickup_status VARCHAR(10) NOT NULL
);

CREATE TABLE WasteDisposal (
    disposal_id INT PRIMARY KEY,
    pickup_id INT NOT NULL,
    disposal_date DATE NOT NULL,
    disposal_method VARCHAR(20) NOT NULL
);

CREATE TABLE trucks (
  truck_id UUID PRIMARY KEY,
  truck_number VARCHAR(50) UNIQUE NOT NULL,
  capacity DECIMAL(10, 2) NOT NULL
);

CREATE TABLE drivers (
  driver_id UUID PRIMARY KEY,
  driver_name VARCHAR(100) NOT NULL,
  driver_license VARCHAR(20) NOT NULL
);

CREATE TABLE waste_bins (
  bin_id UUID PRIMARY KEY,
  location VARCHAR(255) NOT NULL,
  capacity DECIMAL(8) NOT NULL,
  status BOOLEAN NOT NULL DEFAULT true
);

CREATE TABLE waste_collection_driver (
  driver_id INT PRIMARY KEY,
  driver_name VARCHAR(255),
  phone_number VARCHAR(20)
);

CREATE TABLE waste_collection_vehicle (
  vehicle_id INT PRIMARY KEY,
  vehicle_type VARCHAR(50),
  license_plate VARCHAR(20)
);

CREATE TABLE waste_collection_bin (
  bin_id INT PRIMARY KEY,
  location VARCHAR(255),
  schedule_id INT
);
