tables.table_schema:
CREATE TABLE waste_info (
  waste_type VARCHAR(255),
  quantity INT,
  location VARCHAR(255)
);

CREATE TABLE waste_types (
  waste_type_id INT PRIMARY KEY,
  waste_type_name VARCHAR(255),
  waste_type_description TEXT
);

CREATE TABLE WasteTypes (
    waste_type_id INT PRIMARY KEY,
    waste_type_name VARCHAR(255) NOT NULL,
    waste_type_description TEXT
);

CREATE TABLE WasteLocations (
    location_id INT PRIMARY KEY,
    location_name VARCHAR(255) NOT NULL,
    location_address VARCHAR(500) NOT NULL,
    waste_type_id INT
);

CREATE TABLE waste_types (
  waste_type_id INT PRIMARY KEY,
  waste_type_name TEXT
);

CREATE TABLE waste_quantities (
  quantity_id INT PRIMARY KEY,
  quantity DECIMAL,
  waste_type_id INT,
  location_id INT
);

CREATE TABLE waste_locations (
  location_id INT PRIMARY KEY,
  location_name TEXT
);

CREATE TABLE waste_reporting (
  report_id INT PRIMARY KEY,
  report_date DATE,
  quantity_id INT,
  location_id INT
);

CREATE TABLE waste_analysis (
  analysis_id INT PRIMARY KEY,
  analysis_date DATE,
  analysis_result TEXT,
  location_id INT
);
