tables.table_schema:
CREATE TABLE waste_reports (
  report_id INT PRIMARY KEY,
  report_date DATE,
  waste_category TEXT,
  waste_amount FLOAT
);

CREATE TABLE compliance_data (
  compliance_id INT PRIMARY KEY,
  facility_name TEXT,
  regulatory_body TEXT,
  compliance_status BOOLEAN
);

CREATE TABLE waste_facilities (
  facility_id INT PRIMARY KEY,
  facility_name VARCHAR,
  facility_address VARCHAR,
  facility_type VARCHAR
);

CREATE TABLE waste_management_reports (
	report_id INT PRIMARY KEY,
	report_date DATE,
	report_text TEXT
);

CREATE TABLE waste_types (
	type_id INT PRIMARY KEY,
	type_name VARCHAR(50)
);

CREATE TABLE waste_generation (
	generation_id INT PRIMARY KEY,
	generation_date DATE,
	generation_amount DECIMAL
);

CREATE TABLE waste_compliance (
	compliance_id INT PRIMARY KEY,
	compliance_date DATE,
	compliance_status VARCHAR(20)
);

CREATE TABLE waste_locations (
	location_id INT PRIMARY KEY,
	location_name VARCHAR(50),
	location_address TEXT
);
