tables.table_schema:
CREATE TABLE waste_management_policy (
  waste_id INT PRIMARY KEY,
  waste_type VARCHAR(255),
  waste_quantity INT,
  government_agency VARCHAR(255)
);

CREATE TABLE agencies (
    agency_id UUID PRIMARY KEY,
    name VARCHAR,
    contact_person VARCHAR,
    email VARCHAR
);

CREATE TABLE waste_management (
    bin_id UUID PRIMARY KEY,
    waste_type VARCHAR,
    location VARCHAR,
    collected_by VARCHAR
);

CREATE TABLE government_agencies (
    agency_id INT PRIMARY KEY,
    agency_name VARCHAR(50),
    contact_person VARCHAR(50),
    contact_email VARCHAR(50)
);

CREATE TABLE waste_collection (
    collection_id INT PRIMARY KEY,
    collection_date DATE,
    waste_type VARCHAR(50),
    quantity DECIMAL
);

CREATE TABLE policy_improvement (
    policy_id INT PRIMARY KEY,
    policy_name VARCHAR(50),
    implementation_date DATE,
    responsible_agency INT
);

CREATE TABLE government_agencies (
    agency_id UUID PRIMARY KEY,
    agency_name TEXT
);

CREATE TABLE waste_facilities (
    facility_id UUID PRIMARY KEY,
    facility_name TEXT,
    location TEXT,
    capacity INT
);

CREATE TABLE collaboration (
    collaboration_id UUID PRIMARY KEY,
    agency_id UUID,
    facility_id UUID,
    collaboration_type TEXT
);
