);

CREATE TABLE UserAchievements (
  achievement_id INT,
  user_id INT,
  goal_id INT,
  date_achieved DATE
);

CREATE TABLE user_preferences (
  id INT PRIMARY KEY,
  user_id INT,
  theme VARCHAR(50),
  notification BOOLEAN
);
