tables.table_schema:
CREATE TABLE workout_classes (
  class_id UUID PRIMARY KEY,
  class_name VARCHAR(50) NOT NULL,
  class_time TIMESTAMP NOT NULL,
  instructor_name VARCHAR(50) NOT NULL
);

CREATE TABLE class_attendance (
  attendance_id UUID PRIMARY KEY,
  class_id UUID REFERENCES workout_classes(class_id),
  attendee_name VARCHAR(50) NOT NULL,
  attendance_time TIMESTAMP NOT NULL
);

CREATE TABLE instructors (
  instructor_id UUID PRIMARY KEY,
  instructor_name VARCHAR(50) NOT NULL,
  instructor_email VARCHAR(50) UNIQUE NOT NULL
);

CREATE TABLE WorkoutClasses (
    class_id INT PRIMARY KEY,
    class_name VARCHAR(50) NOT NULL,
    instructor_name VARCHAR(50) NOT NULL,
    class_schedule TIMESTAMP NOT NULL
);

CREATE TABLE ClassParticipants (
    participant_id INT PRIMARY KEY,
    participant_name VARCHAR(50) NOT NULL,
    participant_email VARCHAR(100),
    class_id INT REFERENCES WorkoutClasses(class_id) ON DELETE CASCADE
);

CREATE TABLE ClassAttendance (
    attendance_id INT PRIMARY KEY,
    participant_id INT REFERENCES ClassParticipants(participant_id) ON DELETE CASCADE,
    class_id INT REFERENCES WorkoutClasses(class_id) ON DELETE CASCADE,
    attendance_time TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);

CREATE TABLE Instructors (
    instructor_id INT PRIMARY KEY,
    instructor_name VARCHAR(50) NOT NULL,
    instructor_email VARCHAR(100)
);

CREATE TABLE students (
    student_id SERIAL PRIMARY KEY,
    student_name VARCHAR(50) NOT NULL,
    student_email VARCHAR(50) NOT NULL
);

CREATE TABLE instructors (
    instructor_id SERIAL PRIMARY KEY,
    instructor_name VARCHAR(50) NOT NULL,
    instructor_email VARCHAR(50) NOT NULL
);

CREATE TABLE class_instructors (
    class_id INT NOT NULL,
    instructor_id INT NOT NULL,
    PRIMARY KEY (class_id, instructor_id)
);
