tables.table_schema:
CREATE TABLE collectors (
    collector_id INT PRIMARY KEY,
    collector_name VARCHAR(50) NOT NULL,
    email VARCHAR(100) UNIQUE,
    location VARCHAR(50)
);

CREATE TABLE collections (
    collection_id INT PRIMARY KEY,
    collector_id INT REFERENCES collectors(collector_id),
    artwork_name VARCHAR(50) NOT NULL,
    artist_name VARCHAR(50),
    purchase_date DATE
);

CREATE TABLE art_collectors (
 collector_id INT PRIMARY KEY,
 collector_name VARCHAR(50) NOT NULL,
 location VARCHAR(100)
);

CREATE TABLE collectors (
    collector_id INT PRIMARY KEY,
    name STRING,
    location STRING
);

CREATE TABLE artists (
    artist_id INT PRIMARY KEY,
    name STRING,
    nationality STRING
);

CREATE TABLE artworks (
    artwork_id INT PRIMARY KEY,
    artist_id INT,
    title STRING,
    year_created INT
);

CREATE TABLE art_collectors (
  collector_id INT PRIMARY KEY,
  collector_name VARCHAR(50) NOT NULL,
  birth_date DATE,
  email VARCHAR(100) UNIQUE
);

CREATE TABLE art_collections (
  collection_id INT PRIMARY KEY,
  collection_name VARCHAR(100) NOT NULL,
  creator_id INT,
  FOREIGN KEY (creator_id) REFERENCES art_collectors(collector_id)
);
