tables.table_schema:
CREATE TABLE art_gallery (
    artist_name VARCHAR,
    artwork_title VARCHAR,
    sale_price DECIMAL,
    sale_date DATE
);

CREATE TABLE auction_house (
    auction_id INT,
    auction_date DATE,
    auction_location VARCHAR
);

CREATE TABLE buyer (
    buyer_id INT,
    buyer_name VARCHAR,
    buyer_country VARCHAR
);

CREATE TABLE artwork_details (
    artwork_id INT,
    artist_name VARCHAR,
    medium VARCHAR,
    creation_year INT
);

CREATE TABLE auction_result (
    auction_id INT,
    artwork_id INT,
    buyer_id INT,
    final_price DECIMAL
);
