tables.table_schema:
CREATE TABLE art_pieces (
    id UUID PRIMARY KEY,
    title STRING,
    artist STRING,
    year INT
);

CREATE TABLE art_history (
    history_id UUID PRIMARY KEY,
    art_piece_id UUID REFERENCES art_pieces(id),
    event_date DATE,
    event_description STRING
);

CREATE TABLE museum_artists (
    artist_id INT PRIMARY KEY,
    artist_name STRING
);

CREATE TABLE museum_art_movement (
    movement_id INT PRIMARY KEY,
    movement_name STRING
);

CREATE TABLE museum_art_piece_movements (
    art_piece_id INT,
    movement_id INT
);

CREATE TABLE Collection (
    collection_id INT PRIMARY KEY,
    collection_name VARCHAR(100),
    curator VARCHAR(100),
    foundation_year INT
);

CREATE TABLE Movement (
    movement_id INT PRIMARY KEY,
    name VARCHAR(100),
    description TEXT
);
