tables.table_schema:
CREATE TABLE art_events (
    event_id INT PRIMARY KEY,
    event_name VARCHAR(255) NOT NULL,
    event_date DATE NOT NULL,
    event_location VARCHAR(255) NOT NULL
);

CREATE TABLE ArtEvents (
    event_id SERIAL PRIMARY KEY,
    event_name VARCHAR(255) NOT NULL,
    event_date DATE NOT NULL,
    event_location VARCHAR(255) NOT NULL
);

CREATE TABLE Attendees (
    attendee_id SERIAL PRIMARY KEY,
    attendee_name VARCHAR(255) NOT NULL,
    attendee_email VARCHAR(255) UNIQUE NOT NULL,
    registration_date TIMESTAMP NOT NULL
);

CREATE TABLE events (
    event_id UUID PRIMARY KEY,
    event_name VARCHAR(255) NOT NULL,
    event_date DATE NOT NULL,
    event_location VARCHAR(255) NOT NULL
);

CREATE TABLE attendees (
    attendee_id UUID PRIMARY KEY,
    attendee_name VARCHAR(255) NOT NULL,
    attendee_email VARCHAR(255) NOT NULL,
    attendee_phone VARCHAR(15) NOT NULL
);

CREATE TABLE registrations (
    registration_id UUID PRIMARY KEY,
    event_id UUID REFERENCES events(event_id),
    attendee_id UUID REFERENCES attendees(attendee_id),
    registration_date TIMESTAMP NOT NULL
);

CREATE TABLE event_artists (
    event_id UUID REFERENCES events(event_id),
    artist_name VARCHAR(255) NOT NULL,
    artist_specialty VARCHAR(255) NOT NULL
);
