tables.table_schema:
CREATE TABLE conservation_reports (
    report_id INT PRIMARY KEY,
    report_title VARCHAR(255),
    conservation_date DATE,
    project_completed BOOLEAN
);

CREATE TABLE conservators (
    conservator_id INT PRIMARY KEY,
    conservator_name VARCHAR(255),
    expertise_area VARCHAR(255),
    start_date DATE
);

CREATE TABLE conservation_materials (
    material_id INT PRIMARY KEY,
    material_name VARCHAR(255),
    quantity INT,
    unit_measure VARCHAR(50)
);

CREATE TABLE Conservators (
  conservator_id INT PRIMARY KEY,
  name VARCHAR(100),
  specialization VARCHAR(100),
  experience_years INT
);

CREATE TABLE ArtConservationNotes (
  note_id INT PRIMARY KEY,
  report_id INT,
  note_text TEXT,
  note_date TIMESTAMP
);
