tables.table_schema:
CREATE TABLE royalties (
    id INT PRIMARY KEY,
    song_id INT,
    songwriter_id INT,
    royalty_amount DECIMAL
);

CREATE TABLE songs (
    id INT PRIMARY KEY,
    title VARCHAR,
    release_date DATE,
    length INT
);

CREATE TABLE songwriters (
    id INT PRIMARY KEY,
    name VARCHAR,
    role VARCHAR
);

CREATE TABLE songs (
  song_id INT PRIMARY KEY,
  song_title VARCHAR(255),
  album_id INT,
  release_date DATE
);

CREATE TABLE songwriters (
  songwriter_id INT PRIMARY KEY,
  songwriter_name VARCHAR(255),
  contact_info VARCHAR(255),
  royalty_percentage DECIMAL
);

CREATE TABLE musicians (
  musician_id INT PRIMARY KEY,
  musician_name VARCHAR(255),
  instrument VARCHAR(50),
  band_name VARCHAR(100)
);

CREATE TABLE songwriters (
    songwriter_id INT PRIMARY KEY,
    songwriter_name VARCHAR(100),
    nationality VARCHAR(50),
    dob DATE
);
