CREATE TABLE IF NOT EXISTS music_tracks (
  track_id SERIAL PRIMARY KEY,
  track_title VARCHAR(255) NOT NULL,
  artist_name VARCHAR(255) NOT NULL,
  album_name VARCHAR(255) NOT NULL
);

CREATE TABLE IF NOT EXISTS songwriting_credits (
  credit_id SERIAL PRIMARY KEY,
  track_id INT REFERENCES music_tracks(track_id),
  songwriter_name VARCHAR(255) NOT NULL,
  role VARCHAR(100) NOT NULL
);

CREATE TABLE lyrics (
    track_id INT PRIMARY KEY,
    track_title TEXT,
    lyrics_content TEXT
);

CREATE TABLE songwriting_credits (
    credit_id INT PRIMARY KEY,
    track_id INT,
    songwriter TEXT,
    role TEXT
);

CREATE TABLE tracks (
    track_id INT PRIMARY KEY,
    track_title TEXT,
    release_date DATE
);

CREATE TABLE tracks (
  track_id INT PRIMARY KEY,
  track_name VARCHAR(255),
  artist_name VARCHAR(255),
  release_date DATE
);

CREATE TABLE lyrics (
  lyric_id INT PRIMARY KEY,
  track_id INT REFERENCES tracks(track_id),
  lyric_text TEXT
);

CREATE TABLE songwriters (
  songwriter_id INT PRIMARY KEY,
  songwriter_name VARCHAR(255)
);

CREATE TABLE track_credits (
  track_id INT REFERENCES tracks(track_id),
  songwriter_id INT REFERENCES songwriters(songwriter_id)
);
