tables.table_schema:
CREATE TABLE genetic_sequences (
    id INT PRIMARY KEY,
    sequence TEXT
);

CREATE TABLE sequence_analysis (
    analysis_id INT PRIMARY KEY,
    sequence_id INT,
    analysis_result TEXT
);

CREATE TABLE genes (
    id BIGINT PRIMARY KEY,
    gene_name VARCHAR(255) NOT NULL,
    sequence TEXT NOT NULL
);

CREATE TABLE researchers (
    researcher_id BIGINT PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    affiliation VARCHAR(255)
);

CREATE TABLE genetic_sequences (
	id SERIAL PRIMARY KEY,
	dna_sequence TEXT NOT NULL,
	protein_sequence TEXT NOT NULL
);

CREATE TABLE research_labs (
	lab_id SERIAL PRIMARY KEY,
	lab_name TEXT NOT NULL,
	location TEXT
);

CREATE TABLE scientists (
	scientist_id SERIAL PRIMARY KEY,
	name TEXT NOT NULL,
	specialization TEXT,
	lab_id INT REFERENCES research_labs(lab_id)
);

CREATE TABLE experiments (
	experiment_id SERIAL PRIMARY KEY,
	start_date DATE,
	end_date DATE,
	researcher_id INT REFERENCES scientists(scientist_id)
);

CREATE TABLE analysis_results (
	result_id SERIAL PRIMARY KEY,
	experiment_id INT REFERENCES experiments(experiment_id),
	analysis_data JSONB
);
