tables.table_schema:
CREATE TABLE gene_expression_levels (
    gene_id INT PRIMARY KEY,
    gene_name VARCHAR(255) NOT NULL,
    tissue_type VARCHAR(100) NOT NULL,
    expression_level FLOAT NOT NULL
);

CREATE TABLE tissue_types (
    tissue_id INT PRIMARY KEY,
    tissue_name VARCHAR(100) NOT NULL
);

CREATE TABLE gene_tissue_mapping (
    gene_id INT,
    tissue_id INT,
    FOREIGN KEY (gene_id) REFERENCES gene_expression_levels(gene_id),
    FOREIGN KEY (tissue_id) REFERENCES tissue_types(tissue_id)
);

CREATE TABLE gene_expression_levels (
    gene_id INT PRIMARY KEY,
    tissue VARCHAR(50),
    expression_level FLOAT
);

CREATE TABLE tissue_information (
    tissue_id INT PRIMARY KEY,
    tissue_name VARCHAR(50),
    description TEXT
);
