CREATE TABLE drugs (
	drug_id INT PRIMARY KEY,
	drug_name VARCHAR(255),
	drug_category VARCHAR(255),
	drug_description TEXT
);

CREATE TABLE drugs_interactions (
  drug_id INT PRIMARY KEY,
  drug_name VARCHAR(255),
  interaction_type VARCHAR(50)
);

CREATE TABLE drug_side_effects (
  side_effect_id INT PRIMARY KEY,
  side_effect_name VARCHAR(255),
  drug_id INT,
  FOREIGN KEY (drug_id) REFERENCES drugs_interactions(drug_id)
);

CREATE TABLE drug_categories (
  category_id INT PRIMARY KEY,
  category_name VARCHAR(255)
);
