tables.table_schema:
CREATE TABLE regulatory_compliance_info (
    id UUID PRIMARY KEY,
    researcher_name VARCHAR,
    research_project_name VARCHAR,
    compliance_status VARCHAR
);

CREATE TABLE regulatory_documents (
    document_id UUID PRIMARY KEY,
    document_name VARCHAR,
    document_type VARCHAR,
    document_owner VARCHAR,
    related_research_project_id UUID
);

CREATE TABLE regulations (code VARCHAR(10) PRIMARY KEY,
  description TEXT,
  compliance_requirement TEXT);
