tables.table_schema:
CREATE TABLE telemedicine_data (
	patient_id INT PRIMARY KEY,
	telemedicine_id INT,
	telemedicine_date DATE,
	telemedicine_notes TEXT
);

CREATE TABLE TelemedicineData (
	patient_id SERIAL PRIMARY KEY,
	symptoms VARCHAR(100),
	diagnosis TEXT
);

CREATE TABLE ElectronicHealthRecords (
	patient_id SERIAL PRIMARY KEY,
	doctor_name VARCHAR(50),
	diagnosis TEXT,
	prescription TEXT
);

CREATE TABLE PatientCareManagement (
	patient_id SERIAL PRIMARY KEY,
	next_appointment_date DATE,
	current_health_status TEXT
);

CREATE TABLE telemedicine_data (
	patient_id INT,
	symptoms VARCHAR(255),
	diagnosis VARCHAR(255),
	prescription VARCHAR(255)
);
