tables.table_schema:
CREATE TABLE RobotSensorData (
    sensor_id INT PRIMARY KEY,
    sensor_type VARCHAR(255),
    sensor_value FLOAT,
    timestamp TIMESTAMP
);

CREATE TABLE RobotLocationData (
    location_id INT PRIMARY KEY,
    latitude FLOAT,
    longitude FLOAT,
    timestamp TIMESTAMP
);

CREATE TABLE RobotEventData (
    event_id INT PRIMARY KEY,
    event_type VARCHAR(255),
    event_description TEXT,
    timestamp TIMESTAMP
);
