tables.table_schema:
CREATE TABLE robot_components (
    component_id SERIAL PRIMARY KEY,
    component_name VARCHAR(50) NOT NULL,
    quantity INT NOT NULL
);

CREATE TABLE component_suppliers (
    supplier_id SERIAL PRIMARY KEY,
    supplier_name VARCHAR(50) NOT NULL,
    component_id INT NOT NULL,
    supplier_address VARCHAR(100)
);

CREATE TABLE robot_components (
    component_id INT PRIMARY KEY,
    component_name VARCHAR(50) NOT NULL,
    quantity INT NOT NULL,
    warehouse_id INT
);

CREATE TABLE warehouses (
    warehouse_id INT PRIMARY KEY,
    warehouse_name VARCHAR(50) NOT NULL,
    location VARCHAR(100)
);

CREATE TABLE RobotComponents (
  component_id INT PRIMARY KEY,
  component_name VARCHAR(255) NOT NULL,
  quantity INT NOT NULL,
  price DECIMAL(10, 2) NOT NULL
);

CREATE TABLE ComponentSuppliers (
  supplier_id INT PRIMARY KEY,
  supplier_name VARCHAR(255) NOT NULL,
  supplier_location VARCHAR(255) NOT NULL,
  contact_email VARCHAR(255) NOT NULL
);

CREATE TABLE RobotComponents (
   component_id INT PRIMARY KEY,
   component_name VARCHAR(50),
   component_type VARCHAR(50),
   quantity INT
);

CREATE TABLE ComponentSuppliers (
   supplier_id INT PRIMARY KEY,
   supplier_name VARCHAR(50),
   supplier_location VARCHAR(100),
   contact_email VARCHAR(50)
);

CREATE TABLE ComponentInventory (
   inventory_id INT PRIMARY KEY,
   component_id INT,
   quantity_in_stock INT,
   FOREIGN KEY (component_id) REFERENCES RobotComponents(component_id)
);

CREATE TABLE RobotAssemblies (
   assembly_id INT PRIMARY KEY,
   assembly_name VARCHAR(50),
   assembly_date DATE,
   total_components_used INT
);

CREATE TABLE AssemblyWorkers (
   worker_id INT PRIMARY KEY,
   worker_name VARCHAR(50),
   assembly_id INT,
   FOREIGN KEY (assembly_id) REFERENCES RobotAssemblies(assembly_id)
);
