tables.table_schema:
CREATE TABLE energy_consumption_data (
  robot_id UUID PRIMARY KEY,
  energy_consumed FLOAT,
  timestamp TIMESTAMP
);

CREATE TABLE RobotData (
    robot_id INT PRIMARY KEY,
    robot_name VARCHAR(100),
    energy_consumed DECIMAL,
    running_time INT
);

CREATE TABLE EnergyStatistics (
    statistics_id INT PRIMARY KEY,
    robot_id INT,
    energy_consumed DECIMAL,
    date DATE
);

CREATE TABLE Robots (
    id INT PRIMARY KEY,
    robot_name VARCHAR(50),
    energy_consumption DECIMAL(10,2)
);

CREATE TABLE EnergyLogs (
    log_id INT PRIMARY KEY,
    robot_id INT,
    log_date DATE,
    energy_used DECIMAL(10,2)
);

CREATE TABLE MaintenanceRecords (
    record_id INT PRIMARY KEY,
    robot_id INT,
    maintenance_type VARCHAR(50),
    maintenance_date DATE
);

CREATE TABLE RobotOwners (
    owner_id INT PRIMARY KEY,
    robot_id INT,
    owner_name VARCHAR(100),
    owner_email VARCHAR(100)
);

CREATE TABLE Robots (
    robot_id INT PRIMARY KEY,
    robot_name TEXT NOT NULL,
    energy_consumption DECIMAL
);
