tables.table_schema:
CREATE TABLE cloud_logs (
    log_id INT PRIMARY KEY,
    service_name VARCHAR(50),
    log_message TEXT,
    log_timestamp TIMESTAMP
);

CREATE TABLE cloud_services (
    service_id INT PRIMARY KEY,
    service_name VARCHAR(50),
    service_type VARCHAR(50)
);

CREATE TABLE log_analysis (
    analysis_id INT PRIMARY KEY,
    log_id INT,
    service_id INT,
    analysis_results JSONB
);

CREATE TABLE CloudServiceLogs (
	log_id INT PRIMARY KEY,
	service_name VARCHAR(50),
	log_message TEXT,
	log_timestamp TIMESTAMP
);

CREATE TABLE CloudServiceUsers (
	user_id INT PRIMARY KEY,
	username VARCHAR(50),
	email VARCHAR(100),
	role VARCHAR(20)
);

CREATE TABLE CloudServiceSettings (
	setting_id INT PRIMARY KEY,
	setting_name VARCHAR(50),
	setting_value TEXT
);
