tables.table_schema:
CREATE TABLE data_replication (
    id INT PRIMARY KEY,
    region VARCHAR(50),
    data_center VARCHAR(50),
    replicated_data TEXT
);

CREATE TABLE replication_status (
    id INT PRIMARY KEY,
    replication_id INT,
    status VARCHAR(20),
    last_sync TIMESTAMP
);

CREATE TABLE cloud_regions (
  region_id INT PRIMARY KEY,
  region_name VARCHAR(255) NOT NULL,
  data_center VARCHAR(255) NOT NULL
);

CREATE TABLE disaster_recovery (
  recovery_id INT PRIMARY KEY,
  recovery_plan TEXT NOT NULL,
  replication_id INT NOT NULL,
  FOREIGN KEY (replication_id) REFERENCES data_replication(replication_id)
);

CREATE TABLE synchronization_config (
    id SERIAL PRIMARY KEY,
    region VARCHAR(50) NOT NULL,
    data_center VARCHAR(50) NOT NULL,
    replication_method VARCHAR(50) NOT NULL
);

CREATE TABLE region_data (
    id SERIAL PRIMARY KEY,
    region_name VARCHAR(50) NOT NULL,
    data_replication_factor INT NOT NULL,
    data_sync_delay INT
);

CREATE TABLE cloud_regions (
    region_id INT PRIMARY KEY,
    region_name VARCHAR(50) NOT NULL,
    data_center_count INT NOT NULL
);

CREATE TABLE data_centers (
    data_center_id INT PRIMARY KEY,
    data_center_name VARCHAR(50) NOT NULL,
    region_id INT REFERENCES cloud_regions(region_id) ON DELETE CASCADE
);

CREATE TABLE data_replication (
    replication_id INT PRIMARY KEY,
    data_replication_name VARCHAR(50) NOT NULL,
    data_center_id INT REFERENCES data_centers(data_center_id) ON DELETE CASCADE
);

CREATE TABLE replication_jobs (
    job_id INT PRIMARY KEY,
    job_name VARCHAR(50) NOT NULL,
    replication_id INT REFERENCES data_replication(replication_id) ON DELETE CASCADE
);

CREATE TABLE replication_logs (
    log_id INT PRIMARY KEY,
    log_message TEXT NOT NULL,
    job_id INT REFERENCES replication_jobs(job_id) ON DELETE CASCADE
);
