tables.table_schema:
CREATE TABLE stock_data (
    stock_id INT PRIMARY KEY,
    stock_symbol VARCHAR(255) NOT NULL,
    stock_price DECIMAL(10, 2),
    market_capitalization DECIMAL(20, 2)
);

CREATE TABLE market_trends (
    trend_id INT PRIMARY KEY,
    trend_description TEXT,
    trend_start_date DATE,
    trend_end_date DATE
);

CREATE TABLE stock_market_data (
    stock_symbol VARCHAR(10) PRIMARY KEY,
    stock_name VARCHAR(50) NOT NULL,
    market_cap DECIMAL,
    stock_price DECIMAL
);

CREATE TABLE investment_decisions (
    decision_id SERIAL PRIMARY KEY,
    stock_symbol VARCHAR(10) REFERENCES stock_market_data(stock_symbol),
    decision_date DATE,
    decision_type VARCHAR(20) NOT NULL
);

CREATE TABLE trend_analysis (
    analysis_id SERIAL PRIMARY KEY,
    stock_symbol VARCHAR(10) REFERENCES stock_market_data(stock_symbol),
    analysis_date DATE,
    trend VARCHAR(50) NOT NULL
);

CREATE TABLE StockData (
    stock_id INT PRIMARY KEY,
    stock_name VARCHAR(255),
    stock_price DECIMAL(10, 2),
    stock_volume INT
);

CREATE TABLE MarketIndices (
    index_id INT PRIMARY KEY,
    index_name VARCHAR(255),
    index_value DECIMAL(10, 2),
    last_updated TIMESTAMP
);

CREATE TABLE InvestmentDecisions (
    decision_id INT PRIMARY KEY,
    stock_id INT,
    decision_date DATE,
    decision_description TEXT
);
