tables.table_schema:
CREATE TABLE financial_assets (
  asset_id UUID PRIMARY KEY,
  asset_name STRING NOT NULL,
  asset_type STRING,
  asset_value DECIMAL
);

CREATE TABLE Stocks (
    stock_id UUID PRIMARY KEY,
    stock_symbol VARCHAR(10),
    stock_name VARCHAR(50),
    stock_price DECIMAL
);

CREATE TABLE Commodities (
    commodity_id UUID PRIMARY KEY,
    commodity_symbol VARCHAR(10),
    commodity_name VARCHAR(50),
    commodity_price DECIMAL
);
