tables.table_schema:
CREATE TABLE ComplianceCheck (
   id INT PRIMARY KEY,
   check_name VARCHAR,
   check_date DATE,
   department VARCHAR
);

CREATE TABLE Audit (
   id INT PRIMARY KEY,
   audit_name VARCHAR,
   audit_date DATE,
   auditor VARCHAR
);

CREATE TABLE RegulatoryRequirement (
   id INT PRIMARY KEY,
   requirement_name VARCHAR,
   requirement_description TEXT
);

CREATE TABLE compliance_checks (
    check_id INT PRIMARY KEY,
    check_date DATE,
    check_type VARCHAR(50),
    check_result VARCHAR(50)
);

CREATE TABLE compliance_regulations (
    regulation_id INT PRIMARY KEY,
    regulation_name VARCHAR(100),
    regulation_description TEXT
);

CREATE TABLE audit_records (
    record_id INT PRIMARY KEY,
    audit_date DATE,
    auditor_name VARCHAR(50),
    result_notes TEXT
);

CREATE TABLE compliance_issues (
    issue_id INT PRIMARY KEY,
    issue_description TEXT,
    resolution_status VARCHAR(50)
);
