tables.table_schema:
CREATE TABLE energy_consumption_data (
    id INT PRIMARY KEY,
    date DATE,
    energy_usage DECIMAL
);

CREATE TABLE energy_consumption_locations (
    location_id INT PRIMARY KEY,
    location_name VARCHAR,
    city VARCHAR
);

CREATE TABLE energy_consumption_trends (
    trend_id INT PRIMARY KEY,
    trend_name VARCHAR,
    trend_description TEXT
);

CREATE TABLE energy_consumption (
  timestamp TIMESTAMPTZ,
  device_id INT,
  energy_usage DECIMAL
);

CREATE TABLE devices (
  device_id INT PRIMARY KEY,
  device_type VARCHAR,
  location VARCHAR
);

CREATE TABLE energy_sources (
  source_id INT PRIMARY KEY,
  source_name VARCHAR
);

CREATE TABLE energy_trends (
  trend_id SERIAL PRIMARY KEY,
  timestamp TIMESTAMPTZ,
  trend_description TEXT
);

CREATE TABLE energy_consumption_by_month (
    month_id INT,
    year INT,
    energy_usage DECIMAL,
    PRIMARY KEY (month_id, year)
);

CREATE TABLE energy_consumption_by_year (
    year INT,
    total_energy DECIMAL,
    PRIMARY KEY (year)
);

CREATE TABLE energy_consumption_by_region (
    region_id INT,
    year INT,
    energy_usage DECIMAL,
    PRIMARY KEY (region_id, year)
);
