tables.table_schema:
CREATE TABLE virtual_objects (
    object_id UUID PRIMARY KEY,
    object_name VARCHAR(255),
    object_type VARCHAR(50),
    object_properties JSON
);

CREATE TABLE VirtualObjects (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    description TEXT,
    location VARCHAR(100)
);

CREATE TABLE ObjectTags (
    tag_id INT PRIMARY KEY,
    object_id INT,
    tag_name VARCHAR(50),
    FOREIGN KEY (object_id) REFERENCES VirtualObjects(id)
);

CREATE TABLE ObjectImages (
    image_id INT PRIMARY KEY,
    object_id INT,
    image_url VARCHAR(200),
    FOREIGN KEY (object_id) REFERENCES VirtualObjects(id)
);

CREATE TABLE object_tags (
    tag_id UUID PRIMARY KEY,
    object_id UUID REFERENCES virtual_objects(object_id),
    tag_name VARCHAR(50)
);

CREATE TABLE user_tracking (
    user_id UUID PRIMARY KEY,
    last_activity TIMESTAMP
);
