    model_url VARCHAR(255) NOT NULL
);

CREATE TABLE Texture (
    texture_id UUID PRIMARY KEY,
    texture_name VARCHAR(50) NOT NULL,
    texture_url VARCHAR(255) NOT NULL
);

CREATE TABLE ModelTextureMapping (
    mapping_id UUID PRIMARY KEY,
    model_id UUID REFERENCES Model(model_id),
    texture_id UUID REFERENCES Texture(texture_id)
);

CREATE TABLE Models (
    model_id UUID PRIMARY KEY,
    model_name STRING NOT NULL,
    model_file BYTEA NOT NULL
);

CREATE TABLE Textures (
    texture_id UUID PRIMARY KEY,
    texture_name STRING NOT NULL,
    texture_file BYTEA NOT NULL
);

CREATE TABLE ModelTextures (
    model_id UUID REFERENCES Models(model_id),
    texture_id UUID REFERENCES Textures(texture_id),
    PRIMARY KEY(model_id, texture_id)
);

CREATE TABLE Tags (
    tag_id UUID PRIMARY KEY,
    tag_name STRING NOT NULL
);

CREATE TABLE ModelTags (
    model_id UUID REFERENCES Models(model_id),
    tag_id UUID REFERENCES Tags(tag_id),
    PRIMARY KEY(model_id, tag_id)
);
