tables.table_schema:
CREATE TABLE HistoricalDataForTours (
  tour_id INT PRIMARY KEY,
  tour_name VARCHAR(255) NOT NULL,
  tour_date DATE NOT NULL,
  tour_description TEXT
);

CREATE TABLE tours (
  tour_id UUID PRIMARY KEY,
  tour_name VARCHAR(255) NOT NULL,
  tour_description TEXT,
  tour_duration INTERVAL
);

CREATE TABLE tour_locations (
  location_id SERIAL PRIMARY KEY,
  location_name VARCHAR(255) NOT NULL,
  location_description TEXT,
  location_coordinates GEOMETRY(POINT)
);

CREATE TABLE tours (
    tour_id INT PRIMARY KEY,
    tour_name VARCHAR(50) NOT NULL,
    tour_description TEXT,
    tour_location VARCHAR(100) NOT NULL
);

CREATE TABLE tour_guides (
    guide_id INT PRIMARY KEY,
    guide_name VARCHAR(50) NOT NULL,
    guide_phone VARCHAR(15) NOT NULL,
    tour_id INT REFERENCES tours(tour_id)
);

CREATE TABLE tour_guides (
    id INT PRIMARY KEY,
    name TEXT,
    email TEXT
);

CREATE TABLE tour_locations (
    location_id INT PRIMARY KEY,
    location_name TEXT,
    longitude FLOAT,
    latitude FLOAT
);
