tables.table_schema:
CREATE TABLE AppUsage (
  app_id UUID PRIMARY KEY,
  total_users INT,
  total_sessions INT
);

CREATE TABLE UserFeedback (
    userID INT PRIMARY KEY,
    feedback TEXT,
    rating INT
);

CREATE TABLE AppUsers (
    userID INT PRIMARY KEY,
    username TEXT,
    email TEXT
);

CREATE TABLE Applications (
    appID INT PRIMARY KEY,
    appName TEXT,
    category TEXT
);
