tables.table_schema:
CREATE TABLE vulnerabilities (
    vulnerability_id INT PRIMARY KEY,
    vulnerability_name VARCHAR(50) NOT NULL,
    vulnerability_description TEXT,
    severity_level VARCHAR(10) NOT NULL
);

CREATE TABLE vulnerabilities_history (
    history_id INT PRIMARY KEY,
    vulnerability_id INT REFERENCES vulnerabilities(vulnerability_id),
    date_reported TIMESTAMP,
    status VARCHAR(20) NOT NULL
);

CREATE TABLE security_audit (
    audit_id INT PRIMARY KEY,
    audit_type VARCHAR(50) NOT NULL,
    audit_date DATE,
    audit_description TEXT
);
