tables.table_schema:
CREATE TABLE Analyst (
    id INT PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    specialization VARCHAR(50),
    clearance_level VARCHAR(20)
);

CREATE TABLE forensic_cases (
  case_id INT PRIMARY KEY,
  case_name VARCHAR(255),
  case_description TEXT,
  incident_date DATE
);

CREATE TABLE incident_reports ( 
    id UUID PRIMARY KEY, 
    timestamp TIMESTAMP, 
    description TEXT 
);

CREATE TABLE security_incidents (
    incident_id INT PRIMARY KEY,
    incident_type VARCHAR(50),
    incident_date DATE,
    incident_description TEXT
);

CREATE TABLE forensic_investigators (
    investigator_id INT PRIMARY KEY,
    investigator_name VARCHAR(50),
    investigator_role VARCHAR(50),
    investigator_department VARCHAR(50)
);
