tables.table_schema:
CREATE TABLE TimeSeriesData (
    timestamp TIMESTAMP,
    value FLOAT,
    category VARCHAR
);

CREATE TABLE patterns_and_trends (
    id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    value FLOAT
);
CREATE TABLE patterns_metadata (
    metadata_id INT PRIMARY KEY,
    pattern_name TEXT,
    category TEXT
);

CREATE TABLE patterns_data (
    id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    pattern_name VARCHAR(50),
    trend_type VARCHAR(50)
);

CREATE TABLE patterns_mapping (
    mapping_id INT PRIMARY KEY,
    pattern_id INT,
    trend_id INT
);

CREATE TABLE trends_data (
    trend_id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    trend_name VARCHAR(50),
    trend_value FLOAT
);

CREATE TABLE patterns (
    id SERIAL PRIMARY KEY,
    pattern_name TEXT NOT NULL,
    pattern_description TEXT
);

CREATE TABLE trends (
    id SERIAL PRIMARY KEY,
    trend_name TEXT NOT NULL,
    trend_description TEXT
);

CREATE TABLE time_series_data (
    id SERIAL PRIMARY KEY,
    timestamp TIMESTAMPTZ NOT NULL,
    value FLOAT NOT NULL
);

CREATE TABLE queries (
    id SERIAL PRIMARY KEY,
    query_name TEXT NOT NULL,
    query_description TEXT
);

CREATE TABLE results (
    id SERIAL PRIMARY KEY,
    result_name TEXT NOT NULL,
    result_description TEXT
);
