tables.table_schema:
CREATE TABLE KPI_Data (
    id INT PRIMARY KEY,
    date DATE,
    metric1 FLOAT,
    metric2 FLOAT
);

CREATE TABLE KPI_Config (
    config_id INT PRIMARY KEY,
    metric_name VARCHAR(50),
    weight FLOAT
);

CREATE TABLE kpi_metrics (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    value DECIMAL(10,2),
    date_of_measurement DATE
);

CREATE TABLE kpi_categories (
    category_id INT PRIMARY KEY,
    category_name VARCHAR(255),
    description TEXT
);

CREATE TABLE users (id INT PRIMARY KEY, name VARCHAR(255), email VARCHAR(255), age INT, gender VARCHAR(10));

CREATE TABLE orders (order_id INT PRIMARY KEY, user_id INT, product_id INT, quantity INT, order_date DATE);

CREATE TABLE products (product_id INT PRIMARY KEY, name VARCHAR(255), price DECIMAL(10,2), category VARCHAR(50));

CREATE TABLE sales (sale_id INT PRIMARY KEY, order_id INT, product_id INT, sale_date DATE, amount DECIMAL(10,2));
