tables.table_schema:
CREATE TABLE PatientDiagnosticTests (
  patient_id INT PRIMARY KEY,
  test_date DATE,
  test_name VARCHAR(50),
  test_result VARCHAR(100)
);

CREATE TABLE TestTrends (
  trend_id INT PRIMARY KEY,
  test_name VARCHAR(50),
  trend_value DECIMAL,
  trend_date DATE
);

CREATE TABLE TreatmentPlans (
  plan_id INT PRIMARY KEY,
  patient_id INT,
  treatment_description TEXT,
  treatment_date DATE
);

CREATE TABLE Patient (
  patient_id INT PRIMARY KEY,
  patient_name TEXT,
  age INT,
  gender TEXT
);

CREATE TABLE Test (
  test_id INT PRIMARY KEY,
  test_name TEXT,
  test_description TEXT,
  test_date TIMESTAMP
);

CREATE TABLE TrendAnalysis (
  analysis_id INT PRIMARY KEY,
  result_id INT REFERENCES TestResult(result_id),
  trend_value FLOAT,
  trend_date TIMESTAMP
);

CREATE TABLE TreatmentPlan (
  plan_id INT PRIMARY KEY,
  analysis_id INT REFERENCES TrendAnalysis(analysis_id),
  plan_description TEXT,
  plan_date TIMESTAMP
);
