tables.table_schema:
CREATE TABLE TreatmentPlan ( 
 patient_id INT, 
 health_history TEXT, 
 preferences TEXT 
 );

CREATE TABLE PatientHealthHistory (
  patient_id INT PRIMARY KEY,
  health_condition VARCHAR(255),
  treatment_plan VARCHAR(500)
);

CREATE TABLE PatientPreferences (
  patient_id INT PRIMARY KEY,
  preferred_treatment VARCHAR(255),
  lifestyle_choices VARCHAR(500)
);

CREATE TABLE Patients (
  patient_id SERIAL PRIMARY KEY,
  name VARCHAR(50) NOT NULL,
  age INT NOT NULL,
  gender VARCHAR(10) NOT NULL
);

CREATE TABLE HealthHistory (
  record_id SERIAL PRIMARY KEY,
  patient_id INT REFERENCES Patients(patient_id),
  diagnosis VARCHAR(100) NULL,
  date DATE NOT NULL
);

CREATE TABLE Preferences (
  preference_id SERIAL PRIMARY KEY,
  patient_id INT REFERENCES Patients(patient_id),
  preference_type VARCHAR(50) NOT NULL,
  preference_value VARCHAR(100) NOT NULL
);

CREATE TABLE TreatmentPlans (
  plan_id SERIAL PRIMARY KEY,
  patient_id INT REFERENCES Patients(patient_id),
  plan_details TEXT NULL
);

CREATE TABLE patients (
    patient_id INT PRIMARY KEY,
    name TEXT,
    age INT,
    gender TEXT
);
