    project_completion INT 
);

CREATE TABLE students (
    student_id INT,
    student_name VARCHAR(255),
    class_id INT,
    PRIMARY KEY (student_id)
);

CREATE TABLE discussions (
    discussion_id UUID PRIMARY KEY,
    student_id UUID,
    discussion_topic TEXT,
    participation_count INT
);

