tables.table_schema:
CREATE TABLE traffic_cameras (
    camera_id UUID PRIMARY KEY,
    location VARCHAR(255) NOT NULL,
    status BOOLEAN
);

CREATE TABLE traffic_lights (
    light_id UUID PRIMARY KEY,
    intersection VARCHAR(255) NOT NULL,
    color VARCHAR(10) NOT NULL
);

CREATE TABLE traffic_incidents (
    incident_id UUID PRIMARY KEY,
    date TIMESTAMP NOT NULL,
    location VARCHAR(255) NOT NULL,
    description TEXT
);

CREATE TABLE TrafficCameras (
    camera_id UUID PRIMARY KEY,
    location VARCHAR,
    status VARCHAR
);

CREATE TABLE TrafficSensors (
    sensor_id UUID PRIMARY KEY,
    location VARCHAR,
    traffic_volume INT,
    status VARCHAR
);

CREATE TABLE TrafficIncidents (
    incident_id UUID PRIMARY KEY,
    location VARCHAR,
    description VARCHAR,
    severity INT
);

CREATE TABLE TrafficReports (
    report_id UUID PRIMARY KEY,
    report_date DATE,
    reporter VARCHAR,
    description VARCHAR
);
