tables.table_schema:
CREATE TABLE energy_consumption_data (
    city_name VARCHAR(50),
    timestamp TIMESTAMP,
    energy_usage DECIMAL(10,2)
);

CREATE TABLE EnergyConsumptionData (
    city_id INT,
    timestamp TIMESTAMP,
    energy_usage DECIMAL
);

CREATE TABLE CityInfo (
    city_id INT,
    city_name VARCHAR(50),
    population INT,
    total_energy_consumed DECIMAL
);

CREATE TABLE energy_consumption (
    city_id INT NOT NULL,
    date DATE NOT NULL,
    consumption_kwh DECIMAL NOT NULL
);

CREATE TABLE city (
    city_id INT PRIMARY KEY,
    city_name VARCHAR(255) NOT NULL
);

CREATE TABLE building (
    building_id INT PRIMARY KEY,
    city_id INT NOT NULL,
    building_name VARCHAR(255) NOT NULL,
    building_type VARCHAR(50) NOT NULL
);

CREATE TABLE city (
    city_id INT PRIMARY KEY,
    city_name VARCHAR(255) NOT NULL,
    population INT NOT NULL
);

CREATE TABLE building (
    building_id INT PRIMARY KEY,
    city_id INT REFERENCES city(city_id),
    building_name VARCHAR(255) NOT NULL,
    building_type VARCHAR(50)
);

CREATE TABLE energy_meter (
    meter_id INT PRIMARY KEY,
    building_id INT REFERENCES building(building_id),
    meter_location VARCHAR(255) NOT NULL
);

CREATE TABLE energy_usage (
    usage_id INT PRIMARY KEY,
    meter_id INT REFERENCES energy_meter(meter_id),
    usage_date DATE NOT NULL,
    usage_amount DECIMAL(10,2) NOT NULL
);

CREATE TABLE EnergyConsumption ( 
    city_id INT NOT NULL, 
    timestamp TIMESTAMP NOT NULL, 
    energy_value FLOAT NOT NULL, 
    PRIMARY KEY (city_id, timestamp) 
);

CREATE TABLE Cities ( 
    city_id INT PRIMARY KEY, 
    city_name VARCHAR(255) NOT NULL 
);

CREATE TABLE Buildings ( 
    building_id INT PRIMARY KEY, 
    building_name VARCHAR(255) NOT NULL, 
    city_id INT NOT NULL, 
    FOREIGN KEY (city_id) REFERENCES Cities(city_id) 
);

CREATE TABLE Devices ( 
    device_id INT PRIMARY KEY, 
    device_name VARCHAR(255) NOT NULL, 
    building_id INT NOT NULL, 
    FOREIGN KEY (building_id) REFERENCES Buildings(building_id) 
);

CREATE TABLE EnergyProviders ( 
    provider_id INT PRIMARY KEY, 
    provider_name VARCHAR(255) NOT NULL 
);
