tables.table_schema:
CREATE TABLE waste_collection (
    collection_id INT PRIMARY KEY,
    schedule_date DATE,
    route VARCHAR(50)
);

CREATE TABLE waste_collection_schedules (
  schedule_id INT PRIMARY KEY,
  route_id INT,
  schedule_date DATE
);

CREATE TABLE waste_collection_routes (
  route_id INT PRIMARY KEY,
  route_name VARCHAR(50),
  area_id INT
);

CREATE TABLE waste_collection_schedule (
    id INT PRIMARY KEY,
    route_id INT,
    schedule_date DATE
);

CREATE TABLE waste_collection_route (
    route_id INT PRIMARY KEY,
    route_name VARCHAR(50),
    supervisor_id INT
);

CREATE TABLE waste_collection_supervisor (
    supervisor_id INT PRIMARY KEY,
    supervisor_name VARCHAR(50),
    contact_number VARCHAR(15)
);

CREATE TABLE waste_collection_schedules (
    schedule_id UUID PRIMARY KEY,
    schedule_date DATE,
    route_id UUID,
    truck_id UUID
);

CREATE TABLE waste_collection_trucks (
    truck_id UUID PRIMARY KEY,
    truck_number VARCHAR,
    truck_capacity INTEGER,
    current_location VARCHAR
);

CREATE TABLE waste_collection_customers (
    customer_id UUID PRIMARY KEY,
    customer_name VARCHAR,
    address VARCHAR,
    schedule_id UUID
);

CREATE TABLE waste_collection_schedule (
	schedule_id INT PRIMARY KEY,
	area_id INT,
	collection_date DATE,
	collection_time TIME
);

CREATE TABLE waste_collection_route (
	route_id INT PRIMARY KEY,
	route_name VARCHAR(50),
	starting_point VARCHAR(100),
	ending_point VARCHAR(100)
);

CREATE TABLE waste_collection_truck (
	truck_id INT PRIMARY KEY,
		truck_plate_number VARCHAR(20),
		truck_capacity INT,
		current_location VARCHAR(100)
);

CREATE TABLE waste_collection_worker (
	worker_id INT PRIMARY KEY,
		worker_name VARCHAR(50),
		worker_role VARCHAR(50),
		worker_phone VARCHAR(15)
);
