tables.table_schema:
CREATE TABLE forecasts (
    id INT PRIMARY KEY,
    date DATE,
    temperature INT,
    precipitation FLOAT
);

CREATE TABLE city_info (
    id INT PRIMARY KEY,
    city_name VARCHAR(255),
    region VARCHAR(255),
    country VARCHAR(255)
);

CREATE TABLE WeatherPatterns (
    id INT PRIMARY KEY,
    pattern_name VARCHAR(255),
    description TEXT
);

CREATE TABLE CityInformation (
    id INT PRIMARY KEY,
    city_name VARCHAR(255),
    population INT,
    region VARCHAR(255)
);
